import React from 'react'
import { Route, useParams, Link } from 'react-router-dom';
import Comments from '../components/comments/Comments';

const DUMMY_QUOTES = [
  {id: '1', author: 'Max', text: 'Learning react'},
  {id: '2', author: 'Max', text: 'Learning react'},
];

const QuoteDetail = () => {
  const params = useParams();
  const quote = DUMMY_QUOTES.find(quote => quote.id === params.id)
  return (
    <div>
      <HighlightedQuote />
      <Link to={`/quotes/${params.id}/comments`}>Comments</Link>
      <Route path='/quotes/:id/comments'>
        <Comments />  
      </Route>
    </div>
  )
}

export default QuoteDetail
